}
.brand .container .exclusive {
  color: #FCC800;
}
.brand .container .new {
  color: #00BD28;
}
.brand .container .sale {
  color: #FF002E;
}
.brand .container .orange {
  color: #FC6302;
}
.brand .container .wrap {
