/* FOOTER */
footer {
  border-top: 1px solid #eee;
}
footer .container {
  padding-top: 90px;
  padding-bottom: 40px;
}
footer .container .store .row {
  display: flex;
  padding-bottom: 100px;
}
footer .container .store .row .store-item {
  padding: 0 20px;
  width: 480px;
}
footer .container .store .row .store-item .image {
  margin-bottom: 16px;
}
footer .container .store .row .store-item .info .title .en {
  font-size: 13px;
  font-weight: 600;
}
footer .container .store .row .store-item .info .title .address {
  font-family: "Noto Sans KR", sans-serif;
  font-size: 13px;
  font-weight: 600;
}
footer .container .store .row .store-item .info .desc {
  font-family: "Noto Sans KR", sans-serif;
  font-size: 13px;
  color: #444;
  margin-top: 14px;
}
footer .container .footer .row {
  display: flex;
  justify-content: space-between;
}
footer .container .footer .row .left {
  display: flex;
  width: 720px;
}
footer .container .footer .row .left .logo {
  flex: 33.333333%;
  max-width: 33.333333%;
  padding: 0 20px;
  box-sizing: border-box;
}
footer .container .footer .row .left .logo img {
  height: 110px;
  margin: 0 auto;
}
footer .container .footer .row .left .info {
  font-size: 13px;
  flex: 0 0 66.666667%;
  max-width: 66.666667%;
  padding: 0 20px;
  box-sizing: border-box;
  font-size: 13px;
  font-family: "Noto Sans KR", sans-serif;
