}
footer .container .footer .row .right .footer-menu .menu {
  width: 180px;
  padding: 0 20px;
}
footer .container .footer .row .right .footer-menu .menu .title {
  font-size: 12px;
  font-weight: 600;
  margin-bottom: 5px;
}
footer .container .footer .row .right .footer-menu .menu .list li {
  font-size: 13px;
  font-family: "Noto Sans KR", sans-serif;
